import React, { Component } from 'react';
import './App.css';
import  CheckBox  from './components/Checkbox/Checkbox';
import { TransitionGroup } from 'react-transition-group';

class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      todos: [
        {id: 1, value: "Task1", isChecked: false},
        {id: 2, value: "Task2", isChecked: true},
        {id: 3, value: "Task3", isChecked: false},
        {id: 4, value: "Task4", isChecked: false}
      ]
    }
  }

  handleCheckElement = (event) => {
    console.log("called");
    let todos = this.state.todos;
    console.log(event.target.value)
    todos.forEach(todo => {
      if (todo.value === event.target.value)
        todo.isChecked =  event.target.checked
    })

    this.setState({todos: todos})
  }

  render() {
    return (
      <div className="App">
        <h1> Check/Uncheck Checkbox with Animation Demo </h1>
        <ul>
        <TransitionGroup className="items-section__list">

        {
          this.state.todos.map((todo) => {
            if(!todo.isChecked)
              return (<CheckBox key={todo.id} handleCheckElement={this.handleCheckElement} {...todo} />);
            else
              return null;
          })
        }
        </TransitionGroup>
        </ul>
      </div>
    );
  }
}

export default App
