import React, { Component } from 'react';
import './App.css';
import  CheckBox  from './components/Checkbox/Checkbox';
import { TransitionGroup } from 'react-transition-group';

class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      todos: [
        {id: 1, value: "Task1", isChecked: false},
        {id: 2, value: "Task2", isChecked: true},
        {id: 3, value: "Task3", isChecked: false},
        {id: 4, value: "Task4", isChecked: false}
      ],
      isLoading:true
    }
  }

  componentDidMount() {
    this.setLoader();
  }

  setLoader(){
    setTimeout(
      function() {
          this.setState({isLoading: false});
      }
      .bind(this),
      1000
    );
  }

  handleCheckElement = (event) => {
    this.setState({isLoading: true})

    let todos = this.state.todos;
    todos.forEach(todo => {
      if (todo.value === event.target.value)
        todo.isChecked =  event.target.checked
    })

    this.setState({todos: todos});
    this.setLoader()
  }

  render() {
    return (
      <div className="App">
        <h1> Check/Uncheck Checkbox with Animation Demo </h1>
        <ul>
        {
          this.state.todos.map((todo) => {
            if(!todo.isChecked)
              return (
                <TransitionGroup className="items-section__list">
                  <CheckBox key={todo.id} isLoading={this.state.isLoading} handleCheckElement={this.handleCheckElement} {...todo} />
                </TransitionGroup>
              );
            else
              return null;
          })
        }
        </ul>
      </div>
    );
  }
}

export default App
