import React from 'react';
import './App.css';
import { arrayOf, PropTypes } from 'prop-types';

const propTypes = {
  todos: arrayOf(PropTypes.shape({})),
};
const defaultProps = {
  todos: [
    {
      title:'Task1',
      completed:true,
    },
    {
      title:'Task2',
      completed:false,
    },
    {
      title:'Task3',
      completed:true,
    }
  ],
};

const App = (props) => {

  let toggleChange = () => {
    this.setState({
      isChecked: !this.state.isChecked,
    });
  }

  let todoComponent = props.todos.map(
    function iterator( todo ) {
        return(
          <li>
            <div className="view">
              <input
                className="toggle"
                type="checkbox"
                checked={todo.completed}
                onChange={this.toggleChange}
                />
              <label>
                {todo.title}
              </label>
            </div>
          </li>
        );
    },
    this
);
  const main = (
    <section className="main">
      <ul className="todo-list">
      {todoComponent}
      </ul>
    </section>
  );
  return (
    <div>
      <header className="header">
        <h1>todos</h1>
      </header>
      {main}
    </div>
  );
}

App.propTypes = propTypes;
App.defaultProps = defaultProps;

export default App;
