import React from 'react';
import './App.css';
import { arrayOf, PropTypes } from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import { green } from '@material-ui/core/colors';
import Checkbox from '@material-ui/core/Checkbox';

const propTypes = {
  todos: arrayOf(PropTypes.shape({})),
};

const defaultProps = {
  todos: [
    {
      title:'Task1',
      completed:true,
    },
    {
      title:'Task2',
      completed:false,
    },
    {
      title:'Task3',
      completed:true,
    }
  ],
};

const App = (props) => {
  const GreenCheckbox = withStyles({
    root: {
      color: green[400],
      '&$checked': {
        color: green[600],
      },
    },
    checked: {},
  })(props => <Checkbox color="default" {...props} />);

  let todoComponent = props.todos.map(
    function iterator( todo ) {
      return(
        <li>
          <div className="view">
            <GreenCheckbox
              checked={todo.completed}
              value="checkedG"
            />
            <label>
              {todo.title}
            </label>
          </div>
        </li>
      );
    },
    this
  );

  const main = (
    <section className="main">
      <ul className="todo-list">
      {todoComponent}
      </ul>
    </section>
  );
  return (
    <div>
      <header className="header">
        <h1>todos</h1>
      </header>
      {main}
    </div>
  );
}

App.propTypes = propTypes;
App.defaultProps = defaultProps;

export default App;
