import React from 'react';
import './App.css';
import PropTypes from 'prop-types';

const propTypes = {
  todos: PropTypes.shape({}),
};
const defaultProps = {
  todos: [
    {
      title:Task1,
      completed:true,
    },
    {
      title:Task2,
      completed:false,
    },
    {
      title:Task3,
      completed:true,
    }
  ],
};

const App = (props) => {
  let todoComponent = props.todos.map(
    function iterator( todo ) {
        return(
          <li>
            <div className="view">
              <input
                className="toggle"
                type="checkbox"
                checked={todo.completed}
              />
              <label>
                {todo.title}
              </label>
            </div>
          </li>
        );
    },
    this
);
  const main = (
    <section className="main">
      <input
        id="toggle-all"
        className="toggle-all"
        type="checkbox"
      />
      <label
        htmlFor="toggle-all"
      />
      <ul className="todo-list">
      {todoComponent}
      </ul>
    </section>
  );
  return (
    <div>
      <header className="header">
        <h1>todos</h1>
      </header>
      {main}
    </div>
  );
}

App.propTypes = propTypes;
App.defaultProps = defaultProps;

export default App;
