import React from 'react'
import './Checkbox.scss';
import { CSSTransition } from 'react-transition-group';

export const CheckBox = props => {
  if(props.isLoading){
    let label = (
      <div className="divLoader">
        <img alt="Loader" src={ loaderImage } />
      </div>
    );
  } else {
    let label = (
      <span className={"label-text" + (props.isChecked ? ' selected-text removed' : '') }>{props.value}</span>
    );
  }
  return (
    <CSSTransition key={props.id} timeout={500} classNames="move">
      <li className="li-box">
        <label>
          <input key={props.id} onChange={props.handleCheckElement} type="checkbox" checked={props.isChecked} value={props.value} />
          {label}
        </label>
      </li>
    </CSSTransition>
  );
}

export default CheckBox
