import React, { Component } from 'react';
import './App.css';
import  CheckBox  from './components/Checkbox/Checkbox';
import { TransitionGroup } from 'react-transition-group';
import loaderImage from '../src/screen.png';

class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      todos: [
        {id: 1, value: "Task1", isChecked: false},
        {id: 2, value: "Task2", isChecked: true},
        {id: 3, value: "Task3", isChecked: false},
        {id: 4, value: "Task4", isChecked: false}
      ],
      isLoading:true
    }
  }

  componentDidMount() {
    setTimeout(this.setState({isLoading: false}), 1000);
  }

  handleCheckElement = (event) => {
    this.setState({isLoading: true})

    console.log("called");
    let todos = this.state.todos;
    console.log(event.target.value)
    todos.forEach(todo => {
      if (todo.value === event.target.value)
        todo.isChecked =  event.target.checked
    })

    this.setState({todos: todos});
    setTimeout(this.setState({isLoading: false}), 1000);
  }

  render() {
    if(this.state.isLoading) {
      return (
        <div class="divLoader">
          <img alt="Loader" src={ loaderImage } />
        </div>
      );
    }
    else {
      return (
        <div className="App">
          <h1> Check/Uncheck Checkbox with Animation Demo </h1>
          <ul>
          {
            this.state.todos.map((todo) => {
              if(!todo.isChecked)
                return (
                  <TransitionGroup className="items-section__list">
                    <CheckBox key={todo.id} handleCheckElement={this.handleCheckElement} {...todo} />
                  </TransitionGroup>
                );
              else
                return null;
            })
          }
          </ul>
        </div>
      );
    }
  }
}

export default App
