import React, { Component } from 'react';
import './App.css';
import  CheckBox  from './components/Checkbox/Checkbox';

class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      todos: [
        {id: 1, value: "Task1", isChecked: false},
        {id: 2, value: "Task2", isChecked: true},
        {id: 3, value: "Task3", isChecked: false},
        {id: 4, value: "Task4", isChecked: false}
      ]
    }
  }

  handleCheckElement = (event) => {
    console.log(event);
    let todos = this.state.todos
    todos.forEach(todo => {
      if (todo.value === event.target.value)
        todo.isChecked =  event.target.checked
    })
    this.setState({todos: todos})
  }

  render() {
    return (
      <div className="App">
      <h1> Check and Uncheck Task </h1>
      <ul>
      {
        this.state.todos.map((todo) => {
          return (<CheckBox handleCheckElement={this.handleCheckElement} {...todo} />)
        })
      }
      </ul>
      </div>
    );
  }
}

export default App
