import React, { Component } from 'react';
import './App.css';
import  CheckBox  from 'components/CheckBox';

class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      todos: [
        {id: 1, value: "Task1", isChecked: false},
        {id: 2, value: "Task2", isChecked: false},
        {id: 3, value: "Task3", isChecked: false},
        {id: 4, value: "Task4", isChecked: false}
      ]
    }
  }

  render() {
    return (
      <div className="App">
      <h1> Check and Uncheck Task </h1>
      <input type="checkbox"  value="checkedall" /> Check / Uncheck All
        <ul>
        {
          this.state.fruites.map((fruite) => {
            return (<CheckBox {...fruite} />)
          })
        }
        </ul>
      </div>
    );
  }
}

export default App


